import * as React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Switch } from 'react-router-dom'
import dayjs from 'dayjs';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import IncomeStatementsTable from './IncomeStatementsTable';
import CandleChart from './CandleChart';

class FormPropsTextFields extends React.Component {
	constructor(props) {
		super(props);
	};

	handleChangeTicker = newValueTicker => this.setState({ valueTicker: newValueTicker });

	render() {
		return (
			<div id="choiceButtons">
				<LocalizationProvider dateAdapter={AdapterDayjs}>
						<TextField
							id="outlined"
							label="Ticker"
							type="text"
							defaultValue="TSLA"
							onChange={(event) => this.handleChangeTicker(event.target.value)}
							inputProps={{ maxLength: 5 }}
						/>
				</LocalizationProvider>
			</div>
		);
	}
}
export default FormPropsTextFields;
