import React, { Component } from 'react';
import './App.css';
import dayjs, { Dayjs } from 'dayjs';
import ReactApexChart from "react-apexcharts";
import FormPropsTextFields from './TickerTextBox.js'

class CandleChart extends React.Component {
	update(e) {
		this.props.changepricingSeries(e.target.pricingSeries);
		this.props.pricingOptions(e.target.pricingOptions);
    }
	render() {
		return (
			<div id="chart" >
			<ReactApexChart options={this.props.pricingOptions} series={this.props.pricingSeries} type="candlestick" />
			</div>
		);
	}
}
export default CandleChart;
