import * as React from 'react';
import { Route, Switch } from "react-router";
import './App.css'
import dayjs, { Dayjs } from 'dayjs';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { CandleChart } from './CandleChart.js'
import { IncomeStatementsTable } from './IncomeStatementsTable.js'
class FormPropsTextFields extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			valueEnd: dayjs('2030-12-31T00:00:00'),
			valueStart: dayjs('2000-01-01T00:00:00'),
			valueTicker: "GOOG",
			pricingSeries: [{
				data: [{}]
			}],
			pricingChartOptions: {
				chart: {
					type: 'candlestick',
				},
				title: {
					text: 'CandleStick Chart',
					align: 'left'
				},
				xaxis: {
					type: 'datetime'
				},
				yaxis: {
					tooltip: {
						enabled: true
					}
				},
				id: props.chartId,
			},
			incomeStatementsSeries: [{
				data: [{}]
			}],
		}
	};
	handleChangeTicker = newValueTicker => this.setState({ valueTicker: newValueTicker });
	handleChangeStartDate = newValueStartDate => this.setState({ valueStart: newValueStartDate });
	handleChangeEndDate = newValueEndDate => this.setState({ valueEnd: newValueEndDate });

	render() {
		return (
			<div id="choiceButtons">
				<LocalizationProvider dateAdapter={AdapterDayjs}>
					<Stack direction="row" spacing={2}>
						<TextField
							id="outlined"
							label="Ticker"
							type="text"
							defaultValue="TSLA"
							onChange={(event) => this.handleChangeTicker(event.target.value)}
							inputProps={{ maxLength: 5 }}
						/>
						<DesktopDatePicker
							label="Start Date"
							inputFormat="MM-DD-YY"
							value={this.state.valueStart}
							onChange={(newValue) => {this.handleChangeStartDate(newValue.format("YYYY-DD-MM"));}}
							renderInput={(params) => <TextField {...params} />}
						/>
						<DesktopDatePicker
							label="End Date"
							inputFormat="MM-DD-YY"
							value={this.state.valueEnd}
							onChange={(newValue) => {this.handleChangeEndDate(newValue.format("YYYY-DD-MM"));}}
							renderInput={(params) => <TextField {...params} />}
						/>
						<Button variant="contained"
							onClick={this.onTrigger} >Generate</Button>
					</Stack>
				</LocalizationProvider>
				<Route>
                <Switch>
                    <Route path="/CandleChart"
                           render={ () =>
                               <default 
							   		valueTicker = {this.state.valueTicker}
                                    valueStart = {this.state.valueStart}
                                    valueEnd = {this.valueEnd}
									handleChangeTicker = {this.handleChangeTicker}
									handleChangeStartDate = {this.handleChangeStartDate}
									handleChangeEndDate = {this.handleChangeEndDate} />
                           } />
                    <Route path="/IncomeStatmentsTable"
                           render={ () =>
							<default 
							valueTicker={this.state.valueTicker}
							valueStart={this.state.valueStart}
							valueEnd={this.valueEnd}
							handleChangeTicker = {this.handleChangeTicker}
							handleChangeStartDate = {this.handleChangeStartDate}
							handleChangeEndDate = {this.handleChangeEndDate}  />
                           } />
                </Switch>
            </Route>
			</div>
		);
	}
}
export default FormPropsTextFields;
