import React, { Component } from 'react';
import './App.css';
import FormPropsTextFields from './TickerTextBox.js';
import ReactApexChart from "react-apexcharts";
class ApexChart extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			series: [{
				data: [{}]
			}],
			options: {
				chart: {
					type: 'candlestick',
				},
				title: {
					text: 'CandleStick Chart',
					align: 'left'
				},
				xaxis: {
					type: 'datetime'
				},
				yaxis: {
					tooltip: {
						enabled: true
					}
				}
			},
		};
	}
	async componentDidMount() {
		const tick = "TSLA";
		const response = await fetch('/api/stocks/' + tick)
		const body = await response.json();
		this.setState({
				series: [{
				data: body.map(day => ({x: new Date(day.date), y:[day.open, day.high, day.low, day.close]}) )
				}],
				options: this.state.options,
			}
		);
	}
	handleCallback = (ticker) => {
		console.log(ticker);
		const body= fetch('/api/stocks/' + ticker).then(
			(response)=>response.json()
			).then(
		this.setState({
			series: [{
				data: body.map(day => ({ x: new Date(day.date), y: [day.open, day.high, day.low, day.close] }))
			}],
			options: this.state.options,
		}
		));
	}
	render() {
		return (
			<div>
			<ReactApexChart options={this.state.options} series={this.state.series} type="candlestick" />
			<FormPropsTextFields parentCallback = {this.handleCallback}/>    
			</div>
		);
	}
}
export default ApexChart;
